$(document).ready(function() {
    $(".nav-button").click(function(e) {
        $("nav.mobile").slideToggle(300)
    }),
    $("nav ul a, .scroll-link").mPageScroll2id({
        offset: 100,
        highlightClass: "mPS2id-clicked"
    }),
    $(".owl-carousel").owlCarousel({
        items: 1,
        loop: !1,
        center: !0,
        margin: 10,
        nav: !0,
        URLhashListener: !0,
        autoplayHoverPause: !1,
        startPosition: "URLHash",
        onTranslate: function(e) {
            var t = e.page.index;
            $(".repairs nav a").removeClass("active"),
            $(".repairs nav a").eq(t).addClass("active")
        }
    }),
    $(".block.two ul li").click(function(e) {
        $(".block.two ul li").removeClass("active"),
        $(this).addClass("active");
        var t = $(this).index() + 1;
        $(".block.two .img img").attr("src", "img/design/" + t + ".jpg")
    }),
    $(".portfolio .bolshe").find("a.btn").click(function(e) {
        $(".portfolio .all-works").slideDown(500),
        $(".portfolio .bolshe").delay(500).hide(500)
    }),
    $(".answers a.btn").click(function(e) {
        $(".hide-question").slideDown(300),
        $(this).delay(300).hide(300)
    }),
    $(".question").click(function(e) {
        $(".answer-box").slideUp(300),
        $(".plus").hasClass("rotate") && $(".plus").removeClass("rotate"),
        $(this).hasClass("active") ? $(this).removeClass("active") : ($(".question").removeClass("active"),
        $(this).next(".answer-box").slideDown(300),
        $(this).addClass("active"),
        $(this).find(".plus").addClass("rotate"))
    }),
    $(".offer .form a.btn").click(function(e) {
        $(this).parents(".form").find("input[type=radio]:checked").hasClass("alert") ? jQuery.magnificPopup.open({
            items: {
                src: "#otkaz"
            },
            type: "inline"
        }) : ($(this).parents(".form").fadeOut(300),
        $(this).parents(".form").next(".form").delay(300).fadeIn(300),
        $(".ellipse.active").last().next(".hr").addClass("active7").next(".ellipse").addClass("active"))
    }),
    $(".block_back").on("click", function() {
        $(this).parents(".form").fadeOut(300),
        $(this).parents(".form").prev(".form").delay(300).fadeIn(300),
        $(".ellipse.active").last().prev(".hr").removeClass("active7").next(".ellipse").removeClass("active")
    }),
    $("#otkaz").click(function(e) {
        $.magnificPopup.close()
    }),
    $(".cena").click(function(e) {
        $("#otkaz p").html("К сожалению, мы беремся за заказы общей стоимостью работ от 20 тыс. руб. и выше.")
    }),
    $(".drugoe").click(function(e) {
        $("#otkaz p").html(" К сожалению, мы работаем только по Туле и Области.")
    });
    $(".navigation");
    var e = $(".vniz").offset().top;
    $(window).scroll(function() {
        $(this).scrollTop() > e ? $(".navigation.fixed").css("transform", "translateY(75px)") : $(".navigation.fixed").css("transform", "translateY(-75px)")
    }),
    $(".popup-with-form").magnificPopup({
        type: "inline",
        preloader: !1,
        fixedContentPos: !0,
        focus: "#name",
        callbacks: {
            beforeOpen: function() {
                $(window).width() < 700 ? this.st.focus = !1 : this.st.focus = "#name"
            }
        }
    }),
    $(".popup-with-politica").magnificPopup({
        type: "inline",
        preloader: !1,
        fixedContentPos: !0,
        mainClass: "politica-class"
    }),
    $(".image_popup_zoom").magnificPopup({
        type: "image",
        zoom: {
            enabled: !0,
            duration: 300
        }
    }),
    $(".zakazat-zvonok").click(function(e) {
        var t = "Заказать звонок";
        $("#test-form h3").html(t),
        $("#test-form .cel").attr("value", t)
    }),
    $(".uznat-cenu").click(function(e) {
        $("#test-form h3").html("Узнать точную цену"),
        $("#test-form .cel").attr("value", "Узнать точную цену")
    }),
    $(".uznat-podrobnee").click(function(e) {
        var t = "Узнать подробнее";
        $("#test-form h3").html("Узнать подробнее"),
        $("#test-form .cel").attr("value", t),
        $("#test-form .cel").attr("value", t)
    }),
    $(".o-proekte").click(function(e) {
        $("#test-form h3").html("Подробнее о проекте"),
        $("#test-form .cel").attr("value", "Подробнее о проекте")
    }),
    $(".zamershika").click(function(e) {
        $("#test-form h3").html("Вызвать замерщика"),
        $("#test-form .cel").attr("value", "Вызвать замерщика")
    }),
    $(".phone").mask("+7(999) 999-99-99", {
        autoclear: !0
    }),
    $("form").submit(function() {
        var e = $(this);
        return $.ajax({
            type: "POST",
            url: "mail.php",
            data: e.serialize()
        }).done(function() {
            jQuery.magnificPopup.open({
                items: {
                    src: "#otpravleno"
                },
                type: "inline"
            }),
            $(".form").fadeOut(300),
            $(".form").eq(0).delay(300).fadeIn(300),
            $(".ellipse.active").removeClass("active").eq(0).addClass("active"),
            $(".hr").removeClass("active7"),
            setTimeout(function() {
                e.trigger("reset")
            }, 1e3)
        }),
        !1
    }),
     setTimeout(function() {
        $(".hide_button").click();
    }, 30000),
    $(document).mousemove(function(e) {

        if(e.pageY <= 1) {
            $(".hide_button").click();
        }

    });
});